import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';
import { getProductsByCategory } from '../data/productsData';
import './CategoryPage.css';

/**
 * ToysPage Component
 * 
 * Displays all products in the Toys & Games category.
 * This page shows individual products with images, descriptions, and prices.
 * 
 * Future enhancements:
 * - Add product filtering and sorting
 * - Add product search functionality
 * - Add product comparison feature
 * - Add customer reviews and ratings
 */
const ToysPage = () => {
  const toys = getProductsByCategory('toys');

  return (
    <div className="category-page">
      {/* Header Component */}
      <Header />
      
      {/* Main Content */}
      <main className="main-content">
        {/* Breadcrumb Navigation */}
        <nav className="breadcrumb">
          <Link to="/" className="breadcrumb-link">Home</Link>
          <span className="breadcrumb-separator">â€º</span>
          <span className="breadcrumb-current">Toys & Games</span>
        </nav>
        
        {/* Page Header */}
        <section className="page-header">
          <div className="page-header-container">
            <h1 className="page-title">Toys & Games</h1>
            <p className="page-description">
              Discover our collection of fun and interactive toys, challenging puzzles, and exciting board games. 
              Perfect for family game nights and endless entertainment.
            </p>
            <div className="product-count">
              {toys.length} products available
            </div>
          </div>
        </section>
        
        {/* Products Grid */}
        <section className="products-section">
          <div className="products-container">
            <div className="products-grid">
              {toys.map((product) => (
                <ProductCard
                  key={product.id}
                  product={product}
                />
              ))}
            </div>
          </div>
        </section>
        
        {/* Call to Action */}
        <section className="cta-section">
          <div className="cta-container">
            <h2 className="cta-title">Need Help Choosing?</h2>
            <p className="cta-description">
              Our team is here to help you find the perfect toys for your family. 
              Contact us for personalized recommendations.
            </p>
            <Link to="/contact" className="cta-button">
              Contact Us
            </Link>
          </div>
        </section>
      </main>
      
      {/* Footer Component */}
      <Footer />
    </div>
  );
};

export default ToysPage;
