import React from 'react';
import './App.css';
import HomePage from './components/HomePage';

/**
 * Main App Component
 * 
 * This is the root component of the Ebis Treasure website.
 * Currently displays the HomePage component, but can be extended
 * to include routing for additional pages in the future.
 * 
 * Future enhancements:
 * - Add React Router for multiple pages
 * - Add state management (Redux/Context) for cart functionality
 * - Add authentication for user accounts
 */
function App() {
  return (
    <div className="App">
      <HomePage />
    </div>
  );
}

export default App;
