import React from 'react';
import './CategoryCard.css';

/**
 * CategoryCard Component
 * 
 * Displays individual product categories as cards.
 * This component is reusable and can be easily updated with new categories.
 * 
 * Props:
 * - title: The category name
 * - description: Brief description of the category
 * - imageSrc: Path to the category image
 * - imageAlt: Alt text for accessibility
 * 
 * Future enhancements:
 * - Add hover effects
 * - Add "View Products" button (when product pages are implemented)
 * - Add category-specific icons
 * - Make images clickable to navigate to category pages
 */
const CategoryCard = ({ title, description, imageSrc, imageAlt }) => {
  return (
    <div className="category-card">
      {/* Category Image */}
      <div className="category-image-container">
        <img 
          src={imageSrc} 
          alt={imageAlt}
          className="category-image"
          loading="lazy"
        />
      </div>
      
      {/* Category Content */}
      <div className="category-content">
        <h3 className="category-title">{title}</h3>
        <p className="category-description">{description}</p>
        
        {/* Future: Add "Explore" button when product pages are implemented */}
        {/* 
        <button className="category-button">
          Explore {title}
        </button>
        */}
      </div>
    </div>
  );
};

export default CategoryCard;
